Method.Implementation:
set %response.ContentType = "application/json; charset=utf-8" do ##class(Ens.Director).CreateBusinessService(..#CLASSBS, .service)
Try { $$$TRACE("Init process") set dynamic = ##class(%Library.DynamicObject).%FromJSON(%request.Content) set pRequest = ##class(Demo.Msg.SpeachRequest).%New() do pRequest.%JSONImport(dynamic) $$$TRACE("Dynamic created") set tSC = service.SendRequestSync(..#BPOPENAI, pRequest, .pResponse) $$$ThrowOnError(tSC) $$$TRACE("Init process") set %response.Status= ..#HTTP200OK set %response.CharSet = "utf-8" set %response.ContentType = "application/json" do pResponse.%JSONExportToStream(.tStream) do tStream.OutputToDevice() } Catch ex { Set tSC=ex.AsStatus() set errorCode = $system.Status.GetErrorCodes(tSC) Set msgError = $System.Status.GetOneStatusText(tSC,1) set pError = ##class(%Library.DynamicObject).%New() set %response.Status= ..#HTTP200OK set %response.CharSet = "utf-8" set %response.ContentType = "application/json" set pError.code = errorCode Set pError.message = msgError write pError.%ToJSON() }
quit $$$OK

